	public static function get_media_placeholder( $media_url, $settings = array( 'disable_js_image_loading' => false ) ) {
		if ( empty( $media_url ) ) {
			return '';
		}
		if ( ! $settings['disable_js_image_loading'] ) {
			return trailingslashit( CFF_PLUGIN_URL ) . 'assets/img/placeholder.png';
		}
		return $media_url;
	}

	public static function avatar_src( $header_data, $feed_options ) {
		if ( CFF_GDPR_Integrations::doing_gdpr( $feed_options )
		     && CFF_GDPR_Integrations::blocking_cdn( $feed_options ) ) {
			return trailingslashit( CFF_PLUGIN_URL ) . 'assets/img/placeholder.png';
		}
		return CFF_Parse_Pro::get_avatar( $header_data );
	}

	public static function cover_image_src( $header_data, $feed_options ) {
		if ( CFF_GDPR_Integrations::doing_gdpr( $feed_options )
			&& CFF_GDPR_Integrations::blocking_cdn( $feed_options ) ) {
			return trailingslashit( CFF_PLUGIN_URL ) . 'assets/img/placeholder.png';
		}
		return CFF_Parse_Pro::get_cover_source( $header_data );
	}

}
